// Loader to copy PROGMEM templates into runtime KWS bank
extern "C" {
  typedef struct { uint16_t T; float* feats; } KwsTemplate;
  typedef struct { uint8_t n; KwsTemplate tpl[3]; } KwsBank; // KWS_MAX_TEMPLATES=3 in firmware
  extern KwsBank g_bank[]; // defined in main.ino
}

static void kws_load_from_progmem() {
  for (int tid=0; tid<44; ++tid) {
    uint16_t count = pgm_read_word(&kws_token_counts[tid]);
    KwsBank &bk = g_bank[tid];
    bk.n = 0;
    if (!count) continue;
    // read T table pointer
    const uint16_t* Ttbl;
    const float* const* Ptbl;
    switch(tid){
      case 0: Ttbl = kws_token_0_T; Ptbl = kws_token_0_ptrs; break;
      case 1: Ttbl = kws_token_1_T; Ptbl = kws_token_1_ptrs; break;
      case 2: Ttbl = kws_token_2_T; Ptbl = kws_token_2_ptrs; break;
      case 3: Ttbl = kws_token_3_T; Ptbl = kws_token_3_ptrs; break;
      case 4: Ttbl = kws_token_4_T; Ptbl = kws_token_4_ptrs; break;
      case 5: Ttbl = kws_token_5_T; Ptbl = kws_token_5_ptrs; break;
      case 6: Ttbl = kws_token_6_T; Ptbl = kws_token_6_ptrs; break;
      case 7: Ttbl = kws_token_7_T; Ptbl = kws_token_7_ptrs; break;
      case 8: Ttbl = kws_token_8_T; Ptbl = kws_token_8_ptrs; break;
      case 9: Ttbl = kws_token_9_T; Ptbl = kws_token_9_ptrs; break;
      case 10: Ttbl = kws_token_10_T; Ptbl = kws_token_10_ptrs; break;
      case 11: Ttbl = kws_token_11_T; Ptbl = kws_token_11_ptrs; break;
      case 12: Ttbl = kws_token_12_T; Ptbl = kws_token_12_ptrs; break;
      case 13: Ttbl = kws_token_13_T; Ptbl = kws_token_13_ptrs; break;
      case 14: Ttbl = kws_token_14_T; Ptbl = kws_token_14_ptrs; break;
      case 15: Ttbl = kws_token_15_T; Ptbl = kws_token_15_ptrs; break;
      case 16: Ttbl = kws_token_16_T; Ptbl = kws_token_16_ptrs; break;
      case 17: Ttbl = kws_token_17_T; Ptbl = kws_token_17_ptrs; break;
      case 18: Ttbl = kws_token_18_T; Ptbl = kws_token_18_ptrs; break;
      case 19: Ttbl = kws_token_19_T; Ptbl = kws_token_19_ptrs; break;
      case 20: Ttbl = kws_token_20_T; Ptbl = kws_token_20_ptrs; break;
      case 21: Ttbl = kws_token_21_T; Ptbl = kws_token_21_ptrs; break;
      case 22: Ttbl = kws_token_22_T; Ptbl = kws_token_22_ptrs; break;
      case 23: Ttbl = kws_token_23_T; Ptbl = kws_token_23_ptrs; break;
      case 24: Ttbl = kws_token_24_T; Ptbl = kws_token_24_ptrs; break;
      case 25: Ttbl = kws_token_25_T; Ptbl = kws_token_25_ptrs; break;
      case 26: Ttbl = kws_token_26_T; Ptbl = kws_token_26_ptrs; break;
      case 27: Ttbl = kws_token_27_T; Ptbl = kws_token_27_ptrs; break;
      case 28: Ttbl = kws_token_28_T; Ptbl = kws_token_28_ptrs; break;
      case 29: Ttbl = kws_token_29_T; Ptbl = kws_token_29_ptrs; break;
      case 30: Ttbl = kws_token_30_T; Ptbl = kws_token_30_ptrs; break;
      case 31: Ttbl = kws_token_31_T; Ptbl = kws_token_31_ptrs; break;
      case 32: Ttbl = kws_token_32_T; Ptbl = kws_token_32_ptrs; break;
      case 33: Ttbl = kws_token_33_T; Ptbl = kws_token_33_ptrs; break;
      case 34: Ttbl = kws_token_34_T; Ptbl = kws_token_34_ptrs; break;
      case 35: Ttbl = kws_token_35_T; Ptbl = kws_token_35_ptrs; break;
      case 36: Ttbl = kws_token_36_T; Ptbl = kws_token_36_ptrs; break;
      case 37: Ttbl = kws_token_37_T; Ptbl = kws_token_37_ptrs; break;
      case 38: Ttbl = kws_token_38_T; Ptbl = kws_token_38_ptrs; break;
      case 39: Ttbl = kws_token_39_T; Ptbl = kws_token_39_ptrs; break;
      case 40: Ttbl = kws_token_40_T; Ptbl = kws_token_40_ptrs; break;
      case 41: Ttbl = kws_token_41_T; Ptbl = kws_token_41_ptrs; break;
      case 42: Ttbl = kws_token_42_T; Ptbl = kws_token_42_ptrs; break;
      case 43: Ttbl = kws_token_43_T; Ptbl = kws_token_43_ptrs; break;
      default: Ttbl=nullptr; Ptbl=nullptr; break;
    }
    if (!Ttbl || !Ptbl) continue;
    uint16_t use = count > 3 ? 3 : count; // cap to firmware slots
    for (uint16_t j=0; j<use; ++j){
      uint16_t T = pgm_read_word(&Ttbl[j]);
      const float* p = (const float*)pgm_read_ptr(&Ptbl[j]);
      size_t bytes = (size_t)T * KWS_NBINS * sizeof(float);
      float* buf = (float*) malloc(bytes);
      if(!buf) continue;
      memcpy_P(buf, p, bytes);
      bk.tpl[j].T = T;
      bk.tpl[j].feats = buf;
      bk.n++;
    }
  }
}
